// SEO content for destinations without guides
// Generated by scripts/generate-destination-seo-content.js

import seoContentData from '../../generated-destination-seo-content.json';

/**
 * Get SEO content for a destination by slug
 * @param {string} slug - Destination slug (e.g., 'loutraki', 'mainz')
 * @returns {Object|null} SEO content object or null if not found
 */
export function getDestinationSeoContent(slug) {
  return seoContentData[slug] || null;
}

/**
 * Get all SEO content
 * @returns {Object} All SEO content indexed by slug
 */
export function getAllDestinationSeoContent() {
  return seoContentData;
}

/**
 * Check if a destination has SEO content
 * @param {string} slug - Destination slug
 * @returns {boolean}
 */
export function hasSeoContent(slug) {
  return slug in seoContentData;
}

